// SettingsScreen.js - экран настроек приложения
import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  Switch,
  TouchableOpacity,
  Alert
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import * as Clipboard from 'expo-clipboard';

const SettingsScreen = () => {
  const [settings, setSettings] = useState({
    notifications: true,
    darkMode: false,
    autoSave: true,
    analytics: true,
    vibration: false
  });

  const toggleSetting = (key) => {
    setSettings(prev => ({
      ...prev,
      [key]: !prev[key]
    }));
  };

  const copyAppInfo = async () => {
    const appInfo = `Simple Counter App\nВерсия: 1.0.0\nРазработчик: Ваше Имя`;
    await Clipboard.setStringAsync(appInfo);
    Alert.alert('Скопировано', 'Информация о приложении скопирована в буфер обмена');
  };

  const resetData = () => {
    Alert.alert(
      'Сброс данных',
      'Вы уверены, что хотите сбросить все данные приложения? Это действие нельзя отменить.',
      [
        { text: 'Отмена', style: 'cancel' },
        { 
          text: 'Сбросить', 
          style: 'destructive',
          onPress: () => {
            Alert.alert('Успех', 'Данные были сброшены');
          }
        },
      ]
    );
  };

  const SettingItem = ({ icon, title, description, value, onToggle, type = 'switch' }) => (
    <View style={styles.settingItem}>
      <View style={styles.settingIcon}>
        <Ionicons name={icon} size={24} color="#007AFF" />
      </View>
      <View style={styles.settingContent}>
        <Text style={styles.settingTitle}>{title}</Text>
        <Text style={styles.settingDescription}>{description}</Text>
      </View>
      {type === 'switch' ? (
        <Switch
          value={value}
          onValueChange={onToggle}
          trackColor={{ false: '#f0f0f0', true: '#007AFF' }}
          thumbColor={value ? '#ffffff' : '#f4f3f4'}
        />
      ) : (
        <Ionicons name="chevron-forward" size={20} color="#c7c7cc" />
      )}
    </View>
  );

  const ActionButton = ({ icon, title, color, onPress }) => (
    <TouchableOpacity style={styles.actionButton} onPress={onPress}>
      <Ionicons name={icon} size={20} color={color} />
      <Text style={[styles.actionButtonText, { color }]}>{title}</Text>
    </TouchableOpacity>
  );

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Настройки</Text>
        <Text style={styles.headerSubtitle}>Управление приложением</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Основные</Text>

        <SettingItem
          icon="notifications"
          title="Уведомления"
          description="Получать уведомления о активности"
          value={settings.notifications}
          onToggle={() => toggleSetting('notifications')}
        />

        <SettingItem
          icon="moon"
          title="Темная тема"
          description="Использовать темную цветовую схему"
          value={settings.darkMode}
          onToggle={() => toggleSetting('darkMode')}
        />

        <SettingItem
          icon="save"
          title="Автосохранение"
          description="Автоматически сохранять данные"
          value={settings.autoSave}
          onToggle={() => toggleSetting('autoSave')}
        />
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Конфиденциальность</Text>

        <SettingItem
          icon="analytics"
          title="Аналитика"
          description="Отправлять анонимные данные об использовании"
          value={settings.analytics}
          onToggle={() => toggleSetting('analytics')}
        />

        <SettingItem
          icon="vibrate"
          title="Вибрация"
          description="Использовать вибрацию при взаимодействии"
          value={settings.vibration}
          onToggle={() => toggleSetting('vibration')}
        />

        <SettingItem
          icon="shield-checkmark"
          title="Конфиденциальность"
          description="Настройки конфиденциальности и безопасности"
          type="navigation"
        />
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Действия</Text>

        <View style={styles.actionsRow}>
          <ActionButton
            icon="copy"
            title="Копировать информацию"
            color="#007AFF"
            onPress={copyAppInfo}
          />
          <ActionButton
            icon="refresh"
            title="Сбросить данные"
            color="#FF3B30"
            onPress={resetData}
          />
        </View>

        <View style={styles.actionsRow}>
          <ActionButton
            icon="help-circle"
            title="Помощь"
            color="#5856D6"
            onPress={() => Alert.alert('Помощь', 'Раздел помощи в разработке')}
          />
          <ActionButton
            icon="star"
            title="Оценить приложение"
            color="#FF9500"
            onPress={() => Alert.alert('Оценка', 'Функция оценки в разработке')}
          />
        </View>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>О приложении</Text>

        <View style={styles.infoItem}>
          <Text style={styles.infoLabel}>Версия</Text>
          <Text style={styles.infoValue}>1.0.0</Text>
        </View>

        <View style={styles.infoItem}>
          <Text style={styles.infoLabel}>Сборка</Text>
          <Text style={styles.infoValue}>2024.01.001</Text>
        </View>

        <View style={styles.infoItem}>
          <Text style={styles.infoLabel}>Разработчик</Text>
          <Text style={styles.infoValue}>Ваше Имя</Text>
        </View>

        <View style={styles.infoItem}>
          <Text style={styles.infoLabel}>Лицензия</Text>
          <Text style={styles.infoValue}>MIT License</Text>
        </View>
      </View>

      <View style={styles.footer}>
        <Text style={styles.footerText}>
          Simple Counter App © 2024{'\n'}
          Сделано с ❤️ для обучения мобильной разработке
        </Text>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    backgroundColor: '#007AFF',
    padding: 20,
    paddingTop: 50,
  },
  headerTitle: {
