// HomeScreen.js - главный экран приложения с улучшенной обработкой ошибок
import React, { useState, useEffect, useCallback } from 'react';
import { 
  View, 
  Text, 
  TouchableOpacity, 
  StyleSheet, 
  ScrollView,
  ActivityIndicator,
  Alert,
  LogBox
} from 'react-native';
import useStorage from '../hooks/useStorage';
import useApi from '../hooks/useApi';
import { apiService } from '../services/apiService';

// Игнорируем специфические предупреждения
LogBox.ignoreLogs([
  'VirtualizedLists should never be nested',
]);

const HomeScreen = ({ navigation }) => {
  // Использование кастомного хука для локального хранилища
  const { 
    value: count, 
    setValue: setCount, 
    loading: storageLoading, 
    error: storageError,
    clearData: clearStorage 
  } = useStorage('counter', 0);

  // Использование кастомного хука для API
  const { 
    data: users, 
    loading: apiLoading, 
    error: apiError, 
    refetch: refetchUsers,
    clearError: clearApiError
  } = useApi('https://jsonplaceholder.typicode.com/users');

  // Локальное состояние для управления UI
  const [localData, setLocalData] = useState([]);
  const [postLoading, setPostLoading] = useState(false);

  // Эффект для обработки ошибок
  useEffect(() => {
    if (storageError) {
      Alert.alert('Ошибка хранилища', storageError);
    }

    if (apiError) {
      // Alert показывается в useApi, здесь только логируем
      console.error('API Error in HomeScreen:', apiError);
    }
  }, [storageError, apiError]);

  // Функция для добавления локальных данных
  const addLocalItem = useCallback(() => {
    try {
      const newItem = {
        id: Date.now() + Math.random(), // Более уникальный ID
        title: `Элемент ${localData.length + 1}`,
        timestamp: new Date().toLocaleTimeString()
      };
      setLocalData(prev => [newItem, ...prev.slice(0, 9)]); // Ограничиваем до 10 элементов
    } catch (error) {
      console.error('Error adding local item:', error);
      Alert.alert('Ошибка', 'Не удалось добавить элемент');
    }
  }, [localData.length]);

  // Функция для имитации создания поста
  const createPost = useCallback(async () => {
    if (postLoading) return;

    setPostLoading(true);
    try {
      const newPost = {
        title: 'Новый пост',
        body: 'Это тестовый пост',
        userId: 1
      };

      // Имитация API вызова с таймаутом
      const result = await Promise.race([
        apiService.createPost(newPost),
        new Promise((_, reject) => 
          setTimeout(() => reject(new Error('Timeout')), 5000)
        )
      ]);

      Alert.alert('Успех', 'Пост создан!');
      console.log('Создан пост:', result);
    } catch (error) {
      console.error('Error creating post:', error);
      Alert.alert('Ошибка', error.message === 'Timeout' 
        ? 'Превышено время ожидания' 
        : 'Не удалось создать пост'
      );
    } finally {
      setPostLoading(false);
    }
  }, [postLoading]);

  // Функция для сброса всех данных
  const resetAll = useCallback(() => {
    Alert.alert(
      'Сброс данных',
      'Вы уверены, что хотите сбросить все данные?',
      [
        { text: 'Отмена', style: 'cancel' },
        { 
          text: 'Сбросить', 
          style: 'destructive',
          onPress: () => {
            setCount(0);
            setLocalData([]);
            clearStorage();
            clearApiError();
          }
        },
      ]
    );
  }, [setCount, clearStorage, clearApiError]);

  // Обработчик ошибок для навигации
  const handleNavigation = useCallback((screenName) => {
    try {
      navigation.navigate(screenName);
    } catch (error) {
      console.error('Navigation error:', error);
      Alert.alert('Ошибка', 'Не удалось перейти на экран');
    }
  }, [navigation]);

  if (storageLoading) {
    return (
      <View style={styles.center}>
        <ActivityIndicator size="large" color="#007AFF" />
        <Text>Загрузка данных...</Text>
      </View>
    );
  }

  return (
    <ScrollView 
      style={styles.container}
      showsVerticalScrollIndicator={false}
    >
      {/* Навигационные кнопки */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Навигация</Text>
        <View style={styles.navRow}>
          <TouchableOpacity 
            style={styles.navButton}
            onPress={() => handleNavigation('Media')}
          >
            <Text style={styles.navButtonText}>Мультимедиа</Text>
          </TouchableOpacity>
          <TouchableOpacity 
            style={styles.navButton}
            onPress={() => handleNavigation('Gallery')}
          >
            <Text style={styles.navButtonText}>Галерея</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.navRow}>
          <TouchableOpacity 
            style={styles.navButton}
            onPress={() => handleNavigation('Analytics')}
          >
            <Text style={styles.navButtonText}>Аналитика</Text>
          </TouchableOpacity>
          <TouchableOpacity 
            style={styles.navButton}
            onPress={() => handleNavigation('Settings')}
          >
            <Text style={styles.navButtonText}>Настройки</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.navRow}>
          <TouchableOpacity 
            style={styles.navButton}
            onPress={() => handleNavigation('PlatformFeatures')}
          >
            <Text style={styles.navButtonText}>Платформы</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Секция счетчика */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Счетчик</Text>
        <Text style={styles.counter}>Текущее значение: {count}</Text>

        <View style={styles.buttonRow}>
          <TouchableOpacity 
            style={styles.button} 
            onPress={() => setCount(count + 1)}
            disabled={storageLoading}
          >
            <Text style={styles.buttonText}>+1</Text>
          </TouchableOpacity>

          <TouchableOpacity 
            style={[styles.button, styles.resetButton]} 
            onPress={() => setCount(0)}
            disabled={storageLoading}
          >
            <Text style={styles.buttonText}>Сбросить</Text>
          </TouchableOpacity>
        </View>

        {storageError && (
          <Text style={styles.errorText}>Ошибка: {storageError}</Text>
        )}
      </View>

      {/* Секция локальных данных */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>
          Локальные данные {localData.length > 0 && `(${localData.length})`}
        </Text>

        <TouchableOpacity 
          style={styles.button} 
          onPress={addLocalItem}
          disabled={localData.length >= 10}
        >
          <Text style={styles.buttonText}>
            {localData.length >= 10 ? 'Достигнут лимит' : 'Добавить элемент'}
          </Text>
        </TouchableOpacity>

        {localData.length === 0 ? (
          <Text style={styles.emptyText}>Нет данных</Text>
        ) : (
          localData.map(item => (
            <View key={item.id} style={styles.item}>
              <Text style={styles.itemTitle}>{item.title}</Text>
              <Text style={styles.timestamp}>{item.timestamp}</Text>
            </View>
          ))
        )}
      </View>

      {/* Секция API данных */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Данные из API</Text>

        <View style={styles.buttonRow}>
          <TouchableOpacity 
            style={styles.button} 
            onPress={refetchUsers}
            disabled={apiLoading}
          >
            <Text style={styles.buttonText}>
              {apiLoading ? 'Загрузка...' : 'Обновить'}
            </Text>
          </TouchableOpacity>

          <TouchableOpacity 
            style={[styles.button, styles.apiButton]} 
            onPress={createPost}
            disabled={postLoading || apiLoading}
          >
            {postLoading ? (
              <ActivityIndicator color="white" size="small" />
            ) : (
              <Text style={styles.buttonText}>Создать пост</Text>
            )}
          </TouchableOpacity>
        </View>

        {apiError && (
          <View style={styles.errorContainer}>
            <Text style={styles.errorText}>Ошибка загрузки данных</Text>
            <TouchableOpacity onPress={clearApiError}>
              <Text style={styles.retryText}>Попробовать снова</Text>
            </TouchableOpacity>
          </View>
        )}

        {apiLoading ? (
          <ActivityIndicator size="small" color="#007AFF" style={styles.loader} />
        ) : users && users.length > 0 ? (
          users.slice(0, 3).map(user => (
            <View key={`user-${user.id}`} style={styles.item}>
              <Text style={styles.userName}>{user.name}</Text>
              <Text style={styles.userEmail}>{user.email}</Text>
            </View>
          ))
        ) : (
          !apiError && <Text style={styles.emptyText}>Нет данных пользователей</Text>
        )}
      </View>

      {/* Кнопка сброса всего */}
      <TouchableOpacity 
        style={[styles.button, styles.dangerButton]} 
        onPress={resetAll}
      >
        <Text style={styles.buttonText}>Сбросить всё</Text>
      </TouchableOpacity>

      {/* Информация о версии */}
      <View style={styles.footer}>
        <Text style={styles.footerText}>Версия 1.0.0</Text>
      </View>
    </ScrollView>
  );
};

