// App.js - главный файл с навигацией и обработкой ошибок
import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { LogBox, Alert } from 'react-native';
import HomeScreen from './src/screens/HomeScreen';
import MediaScreen from './src/screens/MediaScreen';
import GalleryScreen from './src/screens/GalleryScreen';
import AnalyticsScreen from './src/screens/AnalyticsScreen';
import SettingsScreen from './src/screens/SettingsScreen';
import PlatformFeaturesScreen from './src/screens/PlatformFeaturesScreen';

// Игнорируем предупреждения о настройках (для демонстрации)
LogBox.ignoreLogs([
  'Non-serializable values were found in the navigation state',
]);

const Stack = createStackNavigator();

// Компонент для обработки ошибок
const ErrorBoundary = ({ children }) => {
  return children;
};

export default function App() {
  // Функция для обработки ошибок навигации
  const handleNavigationError = (error) => {
    console.error('Navigation Error:', error);
    Alert.alert('Ошибка навигации', 'Попробуйте перезапустить приложение');
  };

  return (
    <ErrorBoundary>
      <NavigationContainer 
        onStateChange={(state) => {
          // Логирование состояния навигации для отладки
          console.log('Navigation State:', state);
        }}
        onUnhandledAction={(action) => {
          console.warn('Unhandled Navigation Action:', action);
        }}
      >
        <Stack.Navigator 
          initialRouteName="Home"
          screenOptions={{
            headerStyle: {
              backgroundColor: '#007AFF',
            },
            headerTintColor: '#fff',
            headerTitleStyle: {
              fontWeight: 'bold',
            },
          }}
          screenListeners={{
            transitionStart: (e) => {
              console.log('Transition started:', e.data.closing ? 'closing' : 'opening');
            },
            transitionEnd: (e) => {
              console.log('Transition completed:', e.data.closing ? 'closed' : 'opened');
            },
          }}
        >
          <Stack.Screen 
            name="Home" 
            component={HomeScreen}
            options={{ title: 'Главная' }}
          />
          <Stack.Screen 
            name="Media" 
            component={MediaScreen}
            options={{ title: 'Мультимедиа' }}
          />
          <Stack.Screen 
            name="Gallery" 
            component={GalleryScreen}
            options={{ title: 'Галерея' }}
          />
          <Stack.Screen 
            name="Analytics" 
            component={AnalyticsScreen}
            options={{ title: 'Аналитика' }}
          />
          <Stack.Screen 
            name="Settings" 
            component={SettingsScreen}
            options={{ title: 'Настройки' }}
          />
          <Stack.Screen 
            name="PlatformFeatures" 
            component={PlatformFeaturesScreen}
            options={{ title: 'Платформенные функции' }}
          />
        </Stack.Navigator>
      </NavigationContainer>
    </ErrorBoundary>
  );
}
