// App.js - главный файл с улучшенной навигацией
import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { Ionicons } from '@expo/vector-icons';
import HomeScreen from './src/screens/HomeScreen';
import MediaScreen from './src/screens/MediaScreen';
import GalleryScreen from './src/screens/GalleryScreen';
import SettingsScreen from './src/screens/SettingsScreen';
import AnalyticsScreen from './src/screens/AnalyticsScreen';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

// Стек навигации для главного экрана
const HomeStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="HomeMain" 
      component={HomeScreen}
      options={{ headerShown: false }}
    />
  </Stack.Navigator>
);

// Стек навигации для медиа
const MediaStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="MediaMain" 
      component={MediaScreen}
      options={{ headerShown: false }}
    />
  </Stack.Navigator>
);

// Главная таб-навигация
const TabNavigator = () => (
  <Tab.Navigator
    screenOptions={({ route }) => ({
      tabBarIcon: ({ focused, color, size }) => {
        let iconName;

        if (route.name === 'Home') {
          iconName = focused ? 'home' : 'home-outline';
        } else if (route.name === 'Media') {
          iconName = focused ? 'play-circle' : 'play-circle-outline';
        } else if (route.name === 'Gallery') {
          iconName = focused ? 'images' : 'images-outline';
        } else if (route.name === 'Analytics') {
          iconName = focused ? 'stats-chart' : 'stats-chart-outline';
        } else if (route.name === 'Settings') {
          iconName = focused ? 'settings' : 'settings-outline';
        }

        return <Ionicons name={iconName} size={size} color={color} />;
      },
      tabBarActiveTintColor: '#007AFF',
      tabBarInactiveTintColor: 'gray',
      tabBarStyle: {
        backgroundColor: 'white',
        borderTopWidth: 1,
        borderTopColor: '#f0f0f0',
        height: 60,
        paddingBottom: 8,
        paddingTop: 8,
      },
      headerStyle: {
        backgroundColor: '#007AFF',
      },
      headerTintColor: '#fff',
      headerTitleStyle: {
        fontWeight: 'bold',
      },
    })}
  >
    <Tab.Screen name="Home" component={HomeStack} options={{ title: 'Главная' }} />
    <Tab.Screen name="Media" component={MediaStack} options={{ title: 'Медиа' }} />
    <Tab.Screen name="Gallery" component={GalleryScreen} options={{ title: 'Галерея' }} />
    <Tab.Screen name="Analytics" component={AnalyticsScreen} options={{ title: 'Аналитика' }} />
    <Tab.Screen name="Settings" component={SettingsScreen} options={{ title: 'Настройки' }} />
  </Tab.Navigator>
);

export default function App() {
  return (
    <NavigationContainer>
      <TabNavigator />
    </NavigationContainer>
  );
}
