// App.js - главный файл с навигацией
import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import HomeScreen from './src/screens/HomeScreen';
import MediaScreen from './src/screens/MediaScreen';
import GalleryScreen from './src/screens/GalleryScreen';
import AnalyticsScreen from './src/screens/AnalyticsScreen';
import SettingsScreen from './src/screens/SettingsScreen';
import PlatformFeaturesScreen from './src/screens/PlatformFeaturesScreen';

const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator 
        initialRouteName="Home"
        screenOptions={{
          headerStyle: {
            backgroundColor: '#007AFF',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
        }}
      >
        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options={{ title: 'Главная' }}
        />
        <Stack.Screen 
          name="Media" 
          component={MediaScreen}
          options={{ title: 'Мультимедиа' }}
        />
        <Stack.Screen 
          name="Gallery" 
          component={GalleryScreen}
          options={{ title: 'Галерея' }}
        />
        <Stack.Screen 
          name="Analytics" 
          component={AnalyticsScreen}
          options={{ title: 'Аналитика' }}
        />
        <Stack.Screen 
          name="Settings" 
          component={SettingsScreen}
          options={{ title: 'Настройки' }}
        />
        <Stack.Screen 
          name="PlatformFeatures" 
          component={PlatformFeaturesScreen}
          options={{ title: 'Платформенные функции' }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
