// MediaScreen.js - экран для работы с мультимедиа
import React, { useState, useRef } from 'react';
import { 
  View, 
  Text, 
  TouchableOpacity, 
  StyleSheet, 
  ScrollView,
  Image,
  Alert 
} from 'react-native';
import { Audio } from 'expo-av';
import * as ImagePicker from 'expo-image-picker';

const MediaScreen = () => {
  const [sound, setSound] = useState();
  const [recording, setRecording] = useState();
  const [recordedURI, setRecordedURI] = useState();
  const [image, setImage] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  // Ссылка на аудио
  const soundRef = useRef();

  // Воспроизведение аудио
  const playSound = async () => {
    try {
      // Используем онлайн источник для звука
      const { sound } = await Audio.Sound.createAsync(
        { uri: 'https://www.soundjay.com/button/beep-07.wav' }
      );
      soundRef.current = sound;
      await sound.playAsync();
      setIsPlaying(true);

      sound.setOnPlaybackStatusUpdate((status) => {
        if (status.didJustFinish) {
          setIsPlaying(false);
        }
      });
    } catch (error) {
      Alert.alert('Ошибка', 'Не удалось воспроизвести аудио');
    }
  };

  // Запись аудио
  const startRecording = async () => {
    try {
      await Audio.requestPermissionsAsync();
      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      });

      // Исправленная константа для записи
      const { recording } = await Audio.Recording.createAsync(
        Audio.RecordingOptionsPresets.HIGH_QUALITY
      );
      setRecording(recording);
    } catch (error) {
      Alert.alert('Ошибка', 'Не удалось начать запись');
    }
  };

  const stopRecording = async () => {
    setRecording(undefined);
    await recording.stopAndUnloadAsync();
    const uri = recording.getURI();
    setRecordedURI(uri);
  };

  // Воспроизведение записи
  const playRecording = async () => {
    try {
      const { sound } = await Audio.Sound.createAsync(
        { uri: recordedURI }
      );
      setSound(sound);
      await sound.playAsync();
    } catch (error) {
      Alert.alert('Ошибка', 'Не удалось воспроизвести запись');
    }
  };

  // Выбор изображения
  const pickImage = async () => {
    try {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });

      if (!result.canceled) {
        setImage(result.assets[0].uri);
      }
    } catch (error) {
      Alert.alert('Ошибка', 'Не удалось выбрать изображение');
    }
  };

  // Сделать фото
  const takePhoto = async () => {
    try {
      const result = await ImagePicker.launchCameraAsync({
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });

      if (!result.canceled) {
        setImage(result.assets[0].uri);
      }
    } catch (error) {
      Alert.alert('Ошибка', 'Не удалось сделать фото');
    }
  };

  return (
    <ScrollView style={styles.container}>
      {/* Секция изображений */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Работа с изображениями</Text>

        <View style={styles.buttonRow}>
          <TouchableOpacity style={styles.button} onPress={pickImage}>
            <Text style={styles.buttonText}>Выбрать фото</Text>
          </TouchableOpacity>

          <TouchableOpacity style={[styles.button, styles.photoButton]} onPress={takePhoto}>
            <Text style={styles.buttonText}>Сделать фото</Text>
          </TouchableOpacity>
        </View>

        {image && (
          <Image source={{ uri: image }} style={styles.image} />
        )}
      </View>

      {/* Секция аудио */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Работа с аудио</Text>

        <TouchableOpacity 
          style={[styles.button, styles.audioButton]} 
          onPress={playSound}
          disabled={isPlaying}
        >
          <Text style={styles.buttonText}>
            {isPlaying ? 'Воспроизводится...' : 'Воспроизвести звук'}
          </Text>
        </TouchableOpacity>

        <View style={styles.buttonRow}>
          <TouchableOpacity 
            style={[styles.button, styles.recordButton]} 
            onPress={recording ? stopRecording : startRecording}
          >
            <Text style={styles.buttonText}>
              {recording ? 'Остановить запись' : 'Начать запись'}
            </Text>
          </TouchableOpacity>

          {recordedURI && (
            <TouchableOpacity 
              style={[styles.button, styles.playButton]} 
              onPress={playRecording}
            >
              <Text style={styles.buttonText}>Воспроизвести запись</Text>
            </TouchableOpacity>
          )}
        </View>
      </View>

      {/* Секция информации */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Информация</Text>
        <Text style={styles.infoText}>
          • Выберите изображение из галереи или сделайте фото{'\n'}
          • Воспроизведите звуковой сигнал{'\n'}
          • Запишите и воспроизведите свой голос
        </Text>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
    padding: 20,
  },
  section: {
    backgroundColor: 'white',
    padding: 15,
    borderRadius: 10,
    marginBottom: 15,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 15,
    color: '#333',
  },
  buttonRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  button: {
    backgroundColor: '#007AFF',
    paddingHorizontal: 15,
    paddingVertical: 12,
    borderRadius: 8,
    alignItems: 'center',
    marginVertical: 5,
    flex: 1,
    marginHorizontal: 5,
  },
  photoButton: {
    backgroundColor: '#34C759',
  },
  audioButton: {
    backgroundColor: '#5856D6',
  },
  recordButton: {
    backgroundColor: '#FF3B30',
  },
  playButton: {
    backgroundColor: '#FF9500',
  },
  buttonText: {
    color: 'white',
    fontSize: 14,
    fontWeight: '600',
    textAlign: 'center',
  },
  image: {
    width: '100%',
    height: 200,
    borderRadius: 8,
    marginTop: 10,
  },
  infoText: {
    fontSize: 14,
    lineHeight: 20,
    color: '#666',
  },
});

export default MediaScreen;
